    def __init__(self, bd='feriados.db'):
        self.conn = sqlite3.connect(bd, detect_types=sqlite3.PARSE_DECLTYPES)
        self.cursor = self.conn

    def insere_dados(self, sql=SQL_DADOS_FERIADOS):
        self.cursor.executemany(sql, self.DADOS_FERIADOS)
        self.conn.commit()

    def create_table(self, sql=SQL_CREATE_TABLE):
        self.cursor.execute(sql)
        self.conn.commit()

    def select_sql(self, sql, dados):
        self.cursor.execute(sql, dados)
        self.result = self.cursor.fetchall()

        return self.result


